import java.io.*
import jxl.*
import jxl.write.*
import java.text.SimpleDateFormat

//get
log.info("=======================test started for get=======================")
def f=new File("C://Mayur//SoapUI//sample.xls");   ///97-2013 workbook xls
def wk= Workbook.getWorkbook(f)
def ws=wk.getSheet("Sheet1")
r=ws.getRows()	//max row number


for(def i=1;i<r;i++)
{
    log.info "last row number - "+r
    Cell c1=ws.getCell(0,i)
    log.info c1.getContents()

    def get = new URL("http://thetestingworldapi.com/api/technicalskills/"+c1.getContents()).openConnection();
    def getRC = get.getResponseCode();
    log.info("code is - "+getRC);
    if(getRC.equals(200)) {
      log.info(get.getInputStream().getText());
    }

}

